validate_doc_update:
function(newDoc, oldDoc, userCtx) {
  if (newDoc.name && newDoc.name.indexOf("_") != -1)
    throw({forbidden: 'The username must not contain an underscore'});
}

user_ids.map:
function(doc) {
  for (var i in doc.roles) {
    var spl = doc.roles[i].split(":");
    if (spl.length == 3 && spl[0] == "user_id")
      emit([spl[1], doc.name], parseInt(spl[2]));
  }
}

user_ids.reduce:
function(keys, values, rereduce) {
  var best = null;
  for (var i in values) {
    if (best === null || best < values[i])
      best = values[i];
  }
  return best;
}
